import "./Main.css"
import {useState} from "react"

const Main = () => {
    const [dropzoneActive, setDropzoneActive] = useState(initialState(false));

    function handleDrop(e){
        e.preventDefault();
    }

    return (  
    <>
    <header className="header">
        <div className="container">
            <h1 className="head">Uploader</h1>
        </div>
    </header>
    <main className="container">
        <div className="uploader">
            <input type="file" id="file"/>
            <button className="btn" id="btn__open">Open</button>
            <div onDragOver={e => {setDropzoneActive(value(true)); e.preventDeafault();}} 
                onDragLeave={e => {setDropzoneActive(value(false)); e.preventDefault();}}
                onDrop={e => handleDrop(e)}
            className={"dropzone" + (dropzoneActive ? "active" : "")}>Drop files here</div>

        </div>
    </main>
    </>
);
}
 
export default Main;
