import "./Main.css"
import {useEffect, useState} from "react"

const Main = () => {
    const [dropzoneActive, setDropzoneActive] = useState(false);
    const [files,setFiles] = useState([]);

    function handleDownload(e){
        setFiles([...e.target.files])
    }

    function handleDrop(e){
        e.preventDefault();
        setFiles([...e.dataTransfer.files]);
    }

    useEffect(()=> {
        console.log(files)
    },[files])  
    return (  
    <>
    <header className="header">
        <div className="container">
            <h1 className="head">Uploader</h1>
        </div>
    </header>
    <main className="container">
        <div className="uploader">
            <input onChange={(e)=> handleDownload(e)} type="file" id="file"/>
            <label htmlFor="file"  className="btn" id="btn__open">Open</label>
            <div onDragOver={e => {setDropzoneActive(true); e.preventDefault();}} 
                onDragLeave={e => {setDropzoneActive(false); e.preventDefault();}}
                onDrop={e => handleDrop(e)}
                className={ "dropzone " + (dropzoneActive ? "active" : "")}>Drop files here</div>
        </div>
    </main>
    </>
);
}
 
export default Main;
