import "./Main.css"
import {useState} from "react"

const Main = () => {
    const [open, triggerInput] = useState(false)
    return (  
    <>
    <header className="header">
        <div className="container">
            <h1 className="head">Uploader</h1>
        </div>
    </header>
    <main className="container">
        <div className="uploader">
            {open &&<input type="file" id="file"/>}
            <button onClick={()=>triggerInput(!open)} className="btn" id="btn__open">Open</button>

        </div>
    </main>
    </>
);
}
 
export default Main;
