import "./Main.css"
import {useEffect, useState} from "react"
import axios from "axios"

const chunkSize = 50 * 1024 * 1024;

const Main = () => {
    const [dropzoneActive, setDropzoneActive] = useState(false);
    const [files,setFiles] = useState([]);
    const [currentFileIndex, setCurrentFileIndex] = useState(null)
    const [lastUploadFileIndex, setLastUploadFileIndex] = useState(null)
    const [currnetChunkIndex, setCurrentChunckIndex] = useState(null)

    function handleDrop(e){
        e.preventDefault();
        setFiles([...files, ...e.dataTransfer.files]);
    }

    function readAndUploadCurrentChunk() {
        const reader = new FileReader();
        const file = files[currentFileIndex];
        if (!file){
            return;
        }
        const from = currnetChunkIndex * chunkSize;
        const to = from + chunkSize;
        const blob = file.slice(from, to);
        reader.onload = e => uploadChunck(e);
        reader.readAsDataURL(blob);
    }

    function uploadChunck(readerEvent){
        const file = files[currentFileIndex];
        const data = readerEvent.target.result;
        const params = new URLSearchParams();
        params.set('name', file.name);
        params.set('size', file.size);
        params.set('currentChunckIndex', currnetChunkIndex);
        params.set('totalChunk', Math.ceil(file.size / chunkSize));
        const headers = {'Content-Type': 'application/octet-stream'};
        const url = 'http//localhost:4001/upload?'+params.toString();
        axios.post(url, data,{headers});
            then(response => {
                const file = files[currentFileIndex];
                const filesize = files[currentFileIndex].size;
                const chunks = Math.ceil(filesize / chunkSize) - 1;
                const isLastChunk = currentChunkIndex === chunks;
                if (isLastChunk) {
                  file.finalFilename = response.data.finalFilename;
                  setLastUploadedFileIndex(currentFileIndex);
                  setCurrentChunkIndex(null);
                } else {
                  setCurrentChunkIndex(currentChunkIndex + 1);
                }
            })
    }

    useEffect(()=> {
        if (files.length > 0){
            if (currentFileIndex === null){
                setCurrentFileIndex(
                    lastUploadFileIndex === null ? 0 : lastUploadFileIndex + 1
                );
            }
        }
    },[files.length]);

    useEffect(() => {
        if (currentFileIndex !== null){
            setCurrentChunkIndex(0)
        }
    }, [currentFileIndex]);

    useEffect(() => {
        if (currnetChunkIndex !==null){
            readAndUploadCurrentChunk();
        }
    }, [currentChunkIndex]);
    return (  
    <>
    <header className="header">
        <div className="container">
            <h1 className="head">Uploader</h1>
        </div>
    </header>
    <main className="container">
        <div className="uploader">
            <input type="file" id="file"/>
            <label htmlFor="file"  className="btn" id="btn__open">Open</label>
            <div onDragOver={e => {setDropzoneActive(true); e.preventDefault();}} 
                onDragLeave={e => {setDropzoneActive(false); e.preventDefault();}}
                onDrop={e => handleDrop(e)}
                className={ "dropzone " + (dropzoneActive ? "active" : "")}>Drop files here</div>
        </div>
    </main>
    </>
);
}
 
export default Main;
